// Parse the input JSON string and split it into four chunks
const rawText = $json.text;

// Remove Markdown JSON code blocks if present
const cleanedText = rawText.replace(/```json|```/g, '').trim();

try {
    const jsonArray = JSON.parse(cleanedText);
    if (!Array.isArray(jsonArray)) {
        throw new Error('The JSON is not an array.');
    }
    const chunkSize = Math.ceil(jsonArray.length / 4);
    const chunks = [];
    for (let i = 0; i < jsonArray.length; i += chunkSize) {
        chunks.push(jsonArray.slice(i, i + chunkSize));
    }
    return chunks.map(chunk => ({ json: { chunk } }));
} catch (error) {
    return [{ json: { error: error.message } }];
}

// Format the organic search results from SerpAPI
const results = $input.first().json.organic_results;
if (results.length === 0) {
  return [{ json: { error: 'No search results found.' } }];
}
const formattedResults = results.map(result => ({
  title: result.title || 'No title available',
  url: result.link || 'No link available',
  source: result.source || result.displayed_link || 'Unknown source'
}));
return formattedResults.map(result => ({ json: result }));

